mapper.select:
select
	member_pw as memberPw
from member where member_id=#{memberId} and member_name=#{memberName} and member_email=#{memberEmail}

select
	member_id as memberId, enroll_date as enrollDate
from member where member_name=#{memberName} and member_phone=#{memberPhone}

mapper.update:
update member set 
	member_phone=#{memberPhone}, member_email=#{memberEmail} 
where member_id=#{memberId}
